import { Component } from '@angular/core';
import { ClaudeAiService } from './claude-ai.service';

interface Question {
  id: string;
  label: string;
  type: 'input' | 'textarea';
}

@Component({
  selector: 'app-task-planner',
  templateUrl: './task-planner.component.html',
  styleUrls: ['./task-planner.component.css']
})
export class TaskPlannerComponent {
  questions: Question[] = [
    { id: "task", label: "What is the main task you're undertaking?", type: "input" },
    { id: "deadline", label: "What is the deadline or time frame for this task?", type: "input" },
    { id: "alignment", label: "How does this task align with your personal or professional goals?", type: "textarea" },
    { id: "purpose", label: "What broader purpose does completing this task serve?", type: "textarea" },
    { id: "subtasks", label: "List 3 smaller, manageable subtasks:", type: "textarea" },
    { id: "challenge", label: "How can you make this task more challenging or interesting?", type: "textarea" },
    { id: "creativity", label: "What creative constraints could you apply to make the task more engaging?", type: "textarea" },
    { id: "skills", label: "What skills could you improve while working on this task?", type: "textarea" },
    { id: "focus_time", label: "How long can you focus on this task before needing a break?", type: "input" },
    { id: "breaks", label: "What type of breaks will be most refreshing for you?", type: "textarea" },
    { id: "gamification", label: "How can you turn aspects of this task into a game or competition with yourself?", type: "textarea" },
    { id: "rewards", label: "What reward system could you implement for task progress?", type: "textarea" },
    { id: "mindfulness", label: "What mindfulness practice will you use before starting work sessions?", type: "textarea" },
    { id: "reflection", label: "How often will you reflect on your progress, and what will you consider during these reflections?", type: "textarea" },
    { id: "obstacles", label: "What challenges do you anticipate in completing this task?", type: "textarea" },
    { id: "strategies", label: "How do you plan to overcome these challenges?", type: "textarea" },
    { id: "success_metrics", label: "How will you measure success in this task?", type: "textarea" },
    { id: "completion", label: 'What does "done" look like for this task?', type: "textarea" },
    { id: "next_action", label: "What is the very next action you need to take to get started?", type: "input" },
  ];

  currentQuestion = 0;
  answers: Record<string, string> = {};
  plan = '';
  isLoading = false;
  error: string | null = null;

  constructor(private claudeAiService: ClaudeAiService) {}

  handleAnswer(answer: string) {
    this.answers[this.questions[this.currentQuestion].id] = answer;
    if (this.currentQuestion < this.questions.length - 1) {
      this.currentQuestion++;
    }
  }

  generatePlan() {
    this.isLoading = true;
    this.error = null;

    const prompt = `
      Based on the following information, create a comprehensive and motivating task plan:

      Task: ${this.answers.task}
      Deadline: ${this.answers.deadline}
      Alignment with goals: ${this.answers.alignment}
      Broader purpose: ${this.answers.purpose}
      Subtasks: ${this.answers.subtasks}
      Challenge: ${this.answers.challenge}
      Creative constraints: ${this.answers.creativity}
      Skills to improve: ${this.answers.skills}
      Focus time: ${this.answers.focus_time}
      Breaks: ${this.answers.breaks}
      Gamification: ${this.answers.gamification}
      Reward system: ${this.answers.rewards}
      Mindfulness practice: ${this.answers.mindfulness}
      Reflection: ${this.answers.reflection}
      Anticipated challenges: ${this.answers.obstacles}
      Strategies to overcome challenges: ${this.answers.strategies}
      Success metrics: ${this.answers.success_metrics}
      Definition of completion: ${this.answers.completion}
      Next action: ${this.answers.next_action}

      Please provide a structured plan that includes:
      1. A brief task overview
      2. The motivation behind the task
      3. A detailed action plan incorporating the provided information
      4. Strategies for maintaining motivation
      5. A reminder about the next immediate action

      Format the response in a clear, easy-to-read manner.
    `;

    this.claudeAiService.generatePlan(prompt).subscribe({
      next: (response) => {
        this.plan = response;
        this.isLoading = false;
      },
      error: (error) => {
        console.error("There was an error generating the plan:", error);
        this.error = "An error occurred while generating the plan. Please try again.";
        this.isLoading = false;
      }
    });
  }
}

// claude-ai.service.ts
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ClaudeAiService {
  private apiUrl = 'https://api.anthropic.com/v1/chat/completions';
  private apiKey = 'YOUR_CLAUDE_AI_API_KEY_HERE'; // Replace with your actual API key

  constructor(private http: HttpClient) {}

  generatePlan(prompt: string): Observable<string> {
    const headers = new HttpHeaders({
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${this.apiKey}`
    });

    const body = {
      model: "claude-3-opus-20240229",  // or whichever model version you're using
      max_tokens: 1000,
      messages: [
        {
          role: "user",
          content: prompt
        }
      ]
    };

    return this.http.post<any>(this.apiUrl, body, { headers }).pipe(
      map(response => response.choices[0].message.content)
    );
  }
}

